export function infiniteScrollArticles({
  containerElement,
  threshold = 200,
  countLinks,
  countLinksUploaded,
  isLoading,
  callback
}) {
  if (countLinks === countLinksUploaded) return;

  let pageOffset = window.pageYOffset + window.innerHeight;
  let containerOffset =
    containerElement.offsetTop + containerElement.clientHeight;

  if (pageOffset + threshold >= containerOffset && !isLoading) {
    callback();
  }
}
