export function articleCreatedAt(value) {
  let date = new Date(value);
  let today = new Date();

  let dateTime = date.toLocaleTimeString(undefined, {
    minute: '2-digit',
    hour: '2-digit'
  });

  if (date.getDate() === today.getDate()) {
    return `Today at ${dateTime}`;
  }

  if (date.getDate() === today.getDate() - 1) {
    return `Yesterday at ${dateTime}`;
  }

  return `${date.toLocaleDateString(undefined, {
    day: '2-digit',
    month: 'long'
  })} ${date.getFullYear()} at ${dateTime}`;
}
