export function articleCreatedAt(value) {
  let date = new Date(value);
  let today = new Date();

  let dateTime = date.toLocaleTimeString(undefined, {
    minute: '2-digit',
    hour: '2-digit'
  });

  if (date.getDate() === today.getDate()) {
    return `Today at <time datetime="${value}">${dateTime}</time>`;
  }

  if (date.getDate() === today.getDate() - 1) {
    return `Yesterday at <time datetime="${value}">${dateTime}</time>`;
  }

  return `<time datetime="${value}">${date.toLocaleDateString(undefined, {
    day: '2-digit',
    month: 'long'
  })} ${date.getFullYear()} at ${dateTime}</time>`;
}

export function tripDateRange(value) {
  let date = new Date(value);
  
  return `<time datetime="${value}">${date.toLocaleDateString()}</time>`;
}
